import React, { useState, useEffect } from "react";
import styles from "../components/styles/slider.module.css";
import Carousel from "@brainhubeu/react-carousel";
import "@brainhubeu/react-carousel/lib/style.css";
import Image from "next/image";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel as Karusel } from "react-responsive-carousel";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import slidesData from "./sliderArray";

const SliderContainer = () => {
  const [nav1, setNav1] = useState(null);
  const [divHeight, setDivHeight] = useState(80);
  const [redImageStyle, setredImageStyle] = useState({ height: "0px" });
  const [change, setChange] = useState(false);
  const [nav2, setNav2] = useState(null);
  const [divIndex, setDivIndex] = useState(null);
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);
  });

  const settingsMain = {
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 3000,
    asNavFor: ".slider-nav",
  };

  const sliderChange = (old, newIndex) => {
    console.log(old);
    console.log(newIndex);

    setDivIndex(newIndex);
  };
  const consoleClick = (e) => {
    console.log(e);
  };
  const lazyLoad = (e) => {
    console.log(e);
    console.log("lazy load");
  };

  const settingsThumbs = {
    slidesToShow: 4,
    slidesToScroll: 1,
    asNavFor: ".slider-for",
    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: "10px",
  };

  return (
    <div className={styles.slider}>
      <Slider
        {...settingsMain}
        onLazyLoad={lazyLoad}
        asNavFor={nav2}
        ref={(slider) => setSlider1(slider)}
        beforeChange={sliderChange}
      >
        {slidesData.map((slide) => (
          <div className={styles.carouselSlide}>
            <div className={styles.carouselImageGradient}></div>
            <div
              className={styles.carouselImage}
              style={{
                backgroundImage: `url(/assets/${slide.id}.jpg)`,
              }}
            ></div>
            <div className={styles.carouselImageText}>
              <span>{slide.categ}</span>
              <h3>{slide.title}</h3>
              <p>{slide.text}</p>
            </div>
          </div>
        ))}
      </Slider>

      <div className="thumbnail-slider-wrap">
        <Slider
          {...settingsThumbs}
          asNavFor={nav1}
          ref={(slider) => setSlider2(slider)}
          className={styles.thumbnail}
        >
          {slidesData.map((slide, index) => (
            <div
              className={styles.slickSlide}
              onClick={() => consoleClick(index)}
              key={slide.id}
              alt={index}
            >
              {/*    <img
                className="slick-slide-image"
                src={`/assets/${slide.id}.jpg`}
              /> */}
              {
                <div
                  className={styles.thumbnailImageRed}
                  style={
                    divIndex === index
                      ? { height: "80%" }
                      : { height: "0", transition: "none" }
                  }
                  key={slide.id}
                ></div>
              }
              <div
                className={styles.thumbnailImageWhite}
                key={slide.text}
              ></div>
              <div
                className={styles.thumbnailImage}
                alt={index}
                style={{
                  backgroundImage: `url(/assets/${slide.id}.jpg)`,
                  after: {
                    content: "",
                    position: "absolute",
                    width: " 2.5px",
                    height: " 100%",
                    backgroundColor: "red",
                    borderRadius: "6px",
                    left: "-5%",
                    transform: "translateY(-50%, -50%)",
                    borderRadius: "4px",
                  },
                }}
              ></div>{" "}
              <div className={styles.thumbnailText}>
                <p>{slide.title}</p>
                <span
                  style={{ backgroundColor: slide.style, color: slide.color }}
                >
                  {slide.thumbnail}
                </span>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
    /*   <div className={styles.slider}>
      <Carousel
        autoPlay={3000}
        className={styles.carousel}
        centered
        lazyLoad={true}
        daggable
        onChange={sliderChange}
      >
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/slider.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-26@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-28@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-57@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
      </Carousel>
    </div> */
  );
};

export default SliderContainer;
