import React, { useState, useEffect } from "react";
import styles from "../components/styles/slider.module.css";
import "@brainhubeu/react-carousel/lib/style.css";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import slidesData from "./sliderArray";

const SliderContainer = () => {
  const [nav1, setNav1] = useState(null);
  const [nav2, setNav2] = useState(null);
  const [divIndex, setDivIndex] = useState(null);
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);
  });

  const settingsMain = {
    dots: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    autoplay: true,
    draggable: true,
    autoplaySpeed: 3500,
    asNavFor: ".slider-nav",
  };

  const sliderChange = (old, newIndex) => {
    setDivIndex(newIndex);
  };

  const onInit = (e) => {
    setDivIndex(0);
  };

  const settingsThumbs = {
    slidesToShow: 4,
    slidesToScroll: 1,
    asNavFor: ".slider-for",
    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: "10px",
  };

  return (
    <div className={styles.slider}>
      <Slider
        {...settingsMain}
        asNavFor={nav2}
        onInit={onInit}
        ref={(slider) => setSlider1(slider)}
        beforeChange={sliderChange}
      >
        {slidesData.map((slide) => (
          <div className={styles.carouselSlide}>
            <div className={styles.carouselImageGradient}></div>
            <div
              className={styles.carouselImage}
              style={{
                backgroundImage: `url(/assets/${slide.id}.jpg)`,
              }}
            ></div>
            <div className={styles.carouselImageText}>
              <span>{slide.categ}</span>
              <h3>{slide.title}</h3>
              <p>{slide.text}</p>
            </div>
          </div>
        ))}
      </Slider>

      <div className="thumbnail-slider-wrap">
        <Slider
          {...settingsThumbs}
          asNavFor={nav1}
          ref={(slider) => setSlider2(slider)}
          className={styles.thumbnail}
        >
          {slidesData.map((slide, index) => (
            <div className={styles.slickSlide} key={slide.id} alt={index}>
              {
                <div
                  className={styles.thumbnailImageRed}
                  style={
                    divIndex === index
                      ? { height: "77%" }
                      : { height: "0", transition: "none" }
                  }
                  key={slide.id}
                ></div>
              }
              <div
                className={styles.thumbnailImageWhite}
                key={slide.text}
              ></div>
              <div
                className={styles.thumbnailImage}
                alt={index}
                style={{
                  backgroundImage: `url(/assets/${slide.id}.jpg)`,
                  after: {
                    content: "",
                    position: "absolute",
                    width: " 2.5px",
                    height: " 100%",
                    backgroundColor: "red",
                    borderRadius: "6px",
                    left: "-5%",
                    transform: "translateY(-50%, -50%)",
                    borderRadius: "4px",
                  },
                }}
              ></div>{" "}
              <div className={styles.thumbnailText}>
                <p>{slide.title}</p>
                <span
                  style={{ backgroundColor: slide.style, color: slide.color }}
                >
                  {slide.thumbnail}
                </span>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default SliderContainer;
