import React, { useState, useEffect } from "react";
import styles from "../components/styles/slider.module.css";
import Carousel from "@brainhubeu/react-carousel";
import "@brainhubeu/react-carousel/lib/style.css";
import Image from "next/image";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel as Karusel } from "react-responsive-carousel";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  lazyLoad: "progressive",
};
const slidesData = [
  {
    id: 1,
    style: "#f03135",
    title: "ხორვატია 2:1 საქართველო",
    categ: "ეროვნული ნაკრები",
    color: "#fff",
    thumbnail: "ერ.ნაკრები",
    text:
      "19 ნოემბერს, საქართველოს ეროვნული ნაკრები წლის ბოლოს შეხვედრაში ხორვატიას დაუპირისპირდა. პულას ალდო დროსინას სახელობის სტადიონზე გამართული ამხანაგური მატჩი ორმხრივი…",
  },
  {
    id: 2,
    title: "U16 | საქართველო 2:0 ბელარუსი",
    categ: "ნაკრები U16",
    style: "#fff",
    thumbnail: "ნაკრები U16",
    color: "#375fc9",
    text: "Officia non provident dolor esse et neque.",
  },
  {
    id: 3,
    title: "ფუტსალი | საქართველო 9:2 ავსტრია",
    categ: " U16",
    style: "#fff",
    thumbnail: "U16",
    color: "#375fc9",
    text: "Ut recusandae vel vitae molestiae id soluta.",
  },
  {
    id: 4,
    title: "ფუტსალი | გერმანია 1:3 საქართველო",
    categ: " U16",
    style: "#fff",
    thumbnail: "U16",
    color: "#375fc9",
    text: "Ut recusandae vel vitae molestiae id soluta.",
  },
];
const SliderContainer = () => {
  const [nav1, setNav1] = useState(null);
  const [nav2, setNav2] = useState(null);
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);
  });
  const settingsMain = {
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    /*  autoplay: true,
    autoplaySpeed: 2000, */
    asNavFor: ".slider-nav",
  };

  const settingsThumbs = {
    slidesToShow: 4,
    slidesToScroll: 1,
    asNavFor: ".slider-for",
    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: "10px",
  };

  return (
    <div className={styles.slider}>
      <Slider
        {...settingsMain}
        asNavFor={nav2}
        ref={(slider) => setSlider1(slider)}
      >
        {slidesData.map((slide) => (
          <div className={styles.carouselSlide}>
            <div className={styles.carouselImageGradient}></div>
            <div
              className={styles.carouselImage}
              style={{
                backgroundImage: `url(/assets/${slide.id}.jpg)`,
              }}
            ></div>
            <div className={styles.carouselImageText}>
              <span>{slide.categ}</span>
              <h3>{slide.title}</h3>
              <p>{slide.text}</p>
            </div>
          </div>
        ))}
      </Slider>

      <div className="thumbnail-slider-wrap">
        <Slider
          {...settingsThumbs}
          asNavFor={nav1}
          ref={(slider) => setSlider2(slider)}
          className={styles.thumbnail}
        >
          {slidesData.map((slide) => (
            <div className={styles.slickSlide} key={slide.id}>
              {/*    <img
                className="slick-slide-image"
                src={`/assets/${slide.id}.jpg`}
              /> */}{" "}
              <div
                className={styles.thumbnailImage}
                style={{
                  backgroundImage: `url(/assets/${slide.id}.jpg)`,
                  "::after": {
                    content: "",
                    position: "absolute",
                    width: " 2.5px",
                    height: " 100%",
                    backgroundColor: "red",
                    borderRadius: "6px",
                    left: "-5%",
                    transform: "translateY(-50%, -50%)",
                    borderRadius: "4px",
                  },
                }}
              ></div>{" "}
              <div className={styles.thumbnailText}>
                <p>{slide.title}</p>
                <span
                  style={{ backgroundColor: slide.style, color: slide.color }}
                >
                  {slide.thumbnail}
                </span>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
    /*   <div className={styles.slider}>
      <Carousel
        autoPlay={3000}
        className={styles.carousel}
        centered
        lazyLoad={true}
        daggable
        onChange={sliderChange}
      >
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/slider.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-26@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-28@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-57@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
      </Carousel>
    </div> */
  );
};

export default SliderContainer;
