import React, { useState, useEffect } from "react";
import styles from "../components/styles/slider.module.css";
import Carousel from "@brainhubeu/react-carousel";
import "@brainhubeu/react-carousel/lib/style.css";
import Image from "next/image";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel as Karusel } from "react-responsive-carousel";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  lazyLoad: "progressive",
};
const slidesData = [
  {
    id: 1,
    title: "repellendus id ullam",
    label: "Dolorem officiis temporibus.",
  },
  {
    id: 2,
    title: "excepturi consequatur est",
    label: "Officia non provident dolor esse et neque.",
  },
  {
    id: 3,
    title: "eius doloribus blanditiis",
    label: "Ut recusandae vel vitae molestiae id soluta.",
  },
];
const SliderContainer = () => {
  const [nav1, setNav1] = useState(null);
  const [nav2, setNav2] = useState(null);
  const [slider1, setSlider1] = useState(null);
  const [slider2, setSlider2] = useState(null);

  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);
  });
  const settingsMain = {
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    autoPlay: true,
    autoplaySpeed: 1000,
    asNavFor: ".slider-nav",
  };

  const settingsThumbs = {
    slidesToShow: 3,
    slidesToScroll: 1,
    asNavFor: ".slider-for",

    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: "10px",
  };
  console.log(nav1);
  console.log(nav2);
  return (
    <div className={styles.slider}>
      <Slider
        {...settingsMain}
        asNavFor={nav2}
        ref={(slider) => setSlider1(slider)}
      >
        {slidesData.map((slide) => (
          <div className={styles.carouselSlide}>
            <img
              className="slick-slide-image"
              src={`/assets/${slide.id}.jpg`}
            />
            {/*       <div
              style={{
                backgroundImage: `url(/assets/${slide.id}.jpg)`,
                width: "100%",
                height: "100%",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundPosition: "center",
              }}
            >
              1
            </div> */}
            <label className="slick-slide-label">{slide.label}</label>
          </div>
        ))}
      </Slider>

      <div className="thumbnail-slider-wrap">
        <Slider
          {...settingsThumbs}
          asNavFor={nav1}
          ref={(slider) => setSlider2(slider)}
        >
          {slidesData.map((slide) => (
            <div className="slick-slide" key={slide.id}>
              <p>{slide.title}</p>
              <img
                className="slick-slide-image"
                src={`/assets/${slide.id}.jpg`}
              />
            </div>
          ))}
        </Slider>
      </div>
    </div>
    /*   <div className={styles.slider}>
      <Carousel
        autoPlay={3000}
        className={styles.carousel}
        centered
        lazyLoad={true}
        daggable
        onChange={sliderChange}
      >
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/slider.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-26@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-28@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
        <div className={styles.carouselSlide}>
          {" "}
          <Image
            src="/assets/image-57@3x.jpg"
            alt="logo"
            height="auto"
            width="auto"
          />
        </div>
      </Carousel>
    </div> */
  );
};

export default SliderContainer;
